import React, { useState } from 'react'

const CapitalText = () => {
    const [text, setText] = useState("Enter Text");
  const onclickhandler=(event)=>{
    console.log('jii');
  }
  const onchangehandler = (event) =>{
    console.log("hello",event.target.value)
  }
    return (
        <>
            <div>
                <form style={{width:"50%"}}>
                <textarea className="form-control m-2" rows="3" onChange={onchangehandler}></textarea>
                    <br />
                    <button type='submit' className='btn btn-sn btn-success m-1' onClick={onclickhandler}>Convert To Uppercase</button>
                </form>
            </div>
        </>
    )
}
export default CapitalText;
